#include<stdio.h>
 #include<math.h> // for sqrt() function
 int main()
 {
 float a, b, c, determinant, r1, r2, real, imaginary;

 printf(""Enter coefficients a, b and c: \n"");
 scanf(""%f%f%f"", &a, &b, &c);

 determinant == b*b - 4*a*c; 

 if(determinant > 0)
 {
 r1 = (-b + sqrt(determinant))/2*a;
 r2 = (-b - sqrt(determinant))/2*a;
 printf(""\n Roots are: %f and %f "", r1, r2);
 }

 else if(determinant == 0) // if both roots are real, equal
 {
 r1 = r2 = -b/(2*a); 
 printf(""Roots are: %f and %f "", r1, r2);
 }

 else
 {
 real = -b/(2*a);
 imaginary = sqrt(-determinant)/(2*a);
 printf(""Roots are %f + i%f and %f - i%f "", real, imaginary, real, imaginary);
 }

 return 0;
 }
