#include<stdio.h>
 int maxfrequency(int arr[], int size)
 {
 int counter = 0;
 int max = 0;
 int index = 0;
 int i,j;

 for(i=0;i<size;i++)
 {
 counter = 0;
 for(j=0;j<size;j++)
 {
 //if repeated check
 if(arr[i] == arr[j])
 counter++;
 }

 if(counter > max)
 {
 max = counter;
 index = i;
 }
 }

//return the maximum repeated element
 return arr[index];
 }

