 int main()
 {
 char s[4][50], t[20];
 int i, j;

 printf(""Enter four strings : \n"");
 for (i = 0; i < 4; i++)
 {
 scanf(""%s"", s[i]);
 }

 for (i = 1; i < 4; i++) {
 for (j = 1; j < 4; j++) {
 if (strcmp(s[j - 1], s[j]) > 0) {
 strcpy(t, s[j - 1]);
 strcpy(s[j - 1], s[j]);
 strcpy(s[j], t);
 }
 }
 }

 printf(""\nStrings in the order are : "");
 for (i = 0; i < 4; i++)
 {
 printf(""\n%s"", s[i]);
 }

 return 0;
 }
