#include<stdio.h>
 int main()
 {

 int x[10][10],y[10][10],z[10][10],i,k,m,a,b,c,d;

 printf(""\n Input the order of matrix x:"");

 scanf(""%d%d"",&a,&b);

 printf(""\n Input the order of matrix y:"");

 scanf(""%d%d"",&c,&d);

 if(b!=c) //check if multiplication possible


printf(""\n Matrix multiplication impossible\n"");

 else

{

printf(""\n Input the elements of matrix x\n""); 
 for(i=0;i<a;i++)

{

 for(k=0;k<b;k++)
 {
 scanf(""%d"",&x[i][k]);
 }
 }

 printf(""\n Input the elements of matrix y \n"");

 for(i=0;i<c;i++)
 {
 for(k=0;k<d;k++)
 {
 scanf(""%d"",&y[i][k]);
 }
 }

 for(i=0;i<a;i++)
 {
 for(k=0;k<d;k++)
 {
 for(m=0;m<b;m++)
 {
 z[i][k]+=(x[i][m]*y[m][k]);
 }
 }
 }

 for(i=0;i<a;i++)

 {

 for(k=0;k<d;k++)
 {
 printf(""%d \t"",z[i][k]);
 }
 printf(""\n"");

}

}

 }
#include <stdio.h>
 int main() 
 {
 int m, n, c, d, matrix[10][10], transpose[10][10];

 printf(""Enter the number of rows and columns of matrix : "");
 scanf(""%d%d"",&m,&n);
 printf(""Enter the elements of matrix : \n"");

 for( c = 0 ; c < m ; c++ )
 {
 for( d = 0 ; d < n ; d++ )
 {
 scanf(""%d"",&matrix[c][d]);
 }
 }

 printf(""Entered matrix is:\n"");

 for( c = 0 ; c < m ; c++ ) 
 {
 for( d = 0 ; d < n ; d++ )
 {
 printf(""%d	"",matrix[c][d]);
 }
 printf(""\n"");
 }


 for( c = 0 ; c < m ; c++ ) 
 {
 for( d = 0 ; d < n ; d++ )
 {
 transpose[d][c] = matrix[c][d];
 }
 }

 printf(""Transpose of matrix :\n"");

 for( c = 0 ; c < n ; c++ )
 {
 for( d = 0 ; d < m ; d++ ) 
 {
 printf(""%d\t"",transpose[c][d]);
 }
 printf(""\n"");
 }

 return 0;
 }
