#include<stdio.h>
 int main()
 {
 int h,m,s,h1,m1,s1,h2,m2,s2,day;

 printf(""Enter first hours,minutes and seconds\n"");

 scanf(""%d%d%d"",&h1,&m1,&s1); 

 printf(""Enter second hours,minutes and seconds\n"");

 scanf(""%d%d%d"",&h2,&m2,&s2);

 s=h=m=day=0;

 s=s1+s2;

 if(s>60)
 {
 m=s/60;
 s=s%60;
 }

 m=m+m1+m2;

 if(m>60)
 {
 h=m/60;
 m=m%60;
 }

 h=h+h1+h2;

 if(h>24)
 {
 day=1;
 h=h%24;
 }

 printf(""First time = %d:%d:%d"",h1,m1,s1);
 printf(""\nSecond time = %d:%d:%d"",h2,m2,s2);
 printf(""\nAdded time ="");

 if(day==0)
 printf("" %d:%d:%d \n"",h,m,s);

 else
 {
 printf(""%d day"",day);
 printf(""%d:%d:%d\n"",h,m,s);
 }
 }

#include <stdio.h>
 struct Distance
 {
 int feet;
 float inch;
 } d1, d2, sumOfDistances;

 int main()
 {
 printf(""Enter first distance\n"");

 printf(""Enter feet: "");
 scanf(""%d"", &d1.feet);
 printf(""Enter inch: "");
 scanf(""%f"", &d1.inch);

 printf(""\nEnter second distance\n"");

 printf(""Enter feet: "");
 scanf(""%d"", &d2.feet);
 printf(""Enter inch: "");
 scanf(""%f"", &d2.inch);

 sumOfDistances.feet = d1.feet+d2.feet;

 sumOfDistances.inch = d1.inch+d2.inch;

 // changing inches it to feet
 if (sumOfDistances.inch>12.0)
 {
 sumOfDistances.inch = sumOfDistances.inch-12.0;
 ++sumOfDistances.feet;
 }

 printf(""\nSum of distances = %d \-%f"" ,sumOfDistances.feet, sumOfDistances.inch);

 return 0;
 }
#include <stdio.h>
 struct complex 
 {

int real, img;

}; 


int main()

{

struct complex a, b, c;


printf(""Enter first complex number\n"");


scanf(""%d"", &a.real);

scanf(""%d"", &a.img);


printf(""Enter second complex number\n"");


scanf(""%d"", &b.real);

scanf(""%d"", &b.img);


c.real = a.real + b.real;

c.img = a.img + b.img;


if ( c.img >= 0 )
 {

printf(""The sum of two complex numbers = %d + %di\n"",c.real,c.img);
 }


else
 {
 printf(""The sum of two complex numbers = %d %di\n"",c.real,c.img);
 }

 return 0;

}
