#include <stdio.h> #include <stdlib.h> //to use exit() function

 int main()
 {
 char c[100];
 FILE *fptr;
 if ((fptr = fopen(""program.txt"", ""r"")) == NULL)
 {
 printf(""EXIT!"");
 exit(1); 
 }

 // reads text of current line

 fscanf(fptr,""%[^\n]"", c);

 printf(""Data in file:\n%s"", c);
 fclose(fptr);

 return 0;
 }
#include<stdio.h>
 struct address
 {
 char name[20];
 char place[20];
 long int pin;
 };

 int main()
 {
 FILE *p;
 struct address x;
 int n,i;

 p=fopen(""address"",""wb"");

 printf(""Enter number of records"");
 scanf(""%d"",&n);
 printf(""Enter %d records:\n"",n);

 for(i=1;i<=n;i++)
 {
 printf(""Enter Name:"");
 scanf(""%s"",x.name);
 printf(""Enter Place:"");
 scanf(""%s"",x.place);
 printf(""Enter Pin code:"");
 scanf(""%ld"",&x.pin);
 fwrite(&x,sizeof(x),1,p);
 }

 fclose(p);

 return 0;
 }
#include <stdio.h>
 #include<stdlib.h>
 struct name {
 int a;
 char c[30];
 };

 int main()
 {
 struct name *ptr;
 int i,n;

 printf(""Enter number of students. : "");
 scanf(""%d"",&n);

 ptr=(struct name*)malloc(n*sizeof(struct name));

 for(i=0;i<n;++i)
 {
 printf(""Enter name and marks:\n"");
 scanf(""%s%d"",&(ptr+i)->c, &(ptr+i)->a);
 }

 printf(""Displaying Data:\n"");

 for(i=0;i<n;++i)
 {
 printf(""%s %d \n"",(ptr+i)->c,(ptr+i)->a);
 }

 return 0;

}
